from game.entities import User, Area, Worldfrom engine.modules.auth.repository import AbstractUserRepositoryfrom engine.repository import Repository, Entity@Entity(User)class UserRepository(AbstractUserRepository):    def set_world(self, uid, wid, iso):        sq = self.session.query(User)        self.session.query(User)\            .filter(User.uid == uid)\            .update({User.wid: wid, User.iso: iso})        #self.session.commit()@Entity(Area)class AreaRepository(Repository):    pass#@Entity(Figure)#class FigureRepository(Repository):#    pass@Entity(World)class WorldRepository(Repository):    def get_first(self):        return self.session.query(World).first()